    public static function icon($id, $className)
    {
        if (array_key_exists($id, self::$icons)) {
            return str_replace('{{ className }}', $className, self::$icons[$id]);
        }

        return '<i class="fa ' . $id . ' ' . $className . '"></i>';
    }

    public static function registerIcon($id, $html)
    {
        if (array_key_exists($id, self::$icons)) {
            throw new Exception("Duplicate Icon ID");
        }
        self::$icons[$id] = $html;
    }
